queryAdvance:
[
  {
    "$match": {
      "course": "@param:course_id"
    }
  },
  {
    "$match": {
      "$expr": {
        "$and": [
          {
            "$cond": {
              "if": {
                "$ne": ["@param:chapter[]", null]
              },
              "then": {
                "$or": [
                  {
                    "$in": ["$chapters", ["@param:chapter[]"]]
                  },
                  {
                    "$in": ["$chapters", [["@param:chapter[]"]]]
                  }
                ]
              },
              "else": true
            }
          }
        ]
      }
    }
  },
  {
    "$addFields": {
      "chapters": {
        "$map": {
          "input": "$chapters",
          "as": "sg",
          "in": {
            "$toObjectId": "$$sg"
          }
        }
      }
    }
  },
  {
    "$lookup": {
      "from": "mge-chapters",
      "localField": "chapters",
      "foreignField": "_id",
      "as": "chapters"
    }
  },
  {
    "$addFields": {
      "video_file": {
        "$cond": {
          "if": {
            "$isArray": "$video_file"
          },
          "then": {
            "$toObjectId": {
              "$arrayElemAt": ["$video_file", 0]
            }
          },
          "else": {
            "$toObjectId": "$video_file"
          }
        }
      }
    }
  },
  {
    "$lookup": {
      "from": "media",
      "localField": "video_file",
      "foreignField": "_id",
      "pipeline": [
        {
          "$addFields": {
            "path": {
              "$concat": [
                {
                  "$cond": [
                    {
                      "$eq": ["minio", "@app_settings:storage_type"]
                    },
                    "@app_settings:minio.public",
                    "@app_settings:doSpace.public"
                  ]
                },
                "/",
                "$disk",
                "/",
                "$filename"
              ]
            }
          }
        }
      ],
      "as": "video_file"
    }
  },
  {
    "$addFields": {
      "is_using": {
        "$cond": {
          "if": {
            "$and": [
              {
                "$ne": ["$chapters", null]
              },
              {
                "$gt": [
                  {
                    "$size": "$chapters"
                  },
                  0
                ]
              }
            ]
          },
          "then": true,
          "else": false
        }
      }
    }
  },
  {
    "$addFields": {
      "is_using_chapter": {
        "$cond": {
          "if": "$is_using",
          "then": "true",
          "else": "false"
        }
      }
    }
  },
  {
    "$match": {
      "$expr": {
        "$cond": {
          "if": {
            "$ne": ["@param:is_using", null]
          },
          "then": {
            "$eq": ["$is_using_chapter", "@param:is_using"]
          },
          "else": true
        }
      }
    }
  },
    {
    "$match": {
      "$expr": {
        "$or": [
          {
            "$eq": ["@param:title", null]
          },
          {
            "$regexMatch": {
              "input": "$title",
              "regex": "@param:title",
              "options": "i"
            }
          }
        ]
      }
    }
  },
  {
    "$project": {
      "is_using_chapter": 0
    }
  },
  {
    "$sort": {
      "created_at": -1
    }
  },
  {
    "$facet": {
      "meta_data": [
        {
          "$count": "count"
        },
        {
          "$addFields": {
            "skip": "@param:skip",
            "limit": "@param:limit"
          }
        }
      ],
      "data": [
        {
          "$skip": "@param:skip"
        },
        {
          "$limit": "@param:limit"
        }
      ]
    }
  }
]
