queryAdvance:
[
  {
    "$match": {
      "course": "@param:course_id"
    }
  },
  {
    "$match": {
      "$expr": {
        "$and": [
          {
            "$cond": {
              "if": {
                "$ne": ["@param:status[]", null]
              },
              "then": {
                "$in": ["$status", "@param:status[]"]
              },
              "else": true
            }
          },
          {
            "$cond": {
              "if": {
                "$ne": ["@param:exam_type[]", null]
              },
              "then": {
                "$eq": ["$exam_type", "@param:exam_type[]"]
              },
              "else": true
            }
          },
          {
            "$cond": {
              "if": {
                "$ne": ["@param:chapter[]", null]
              },
              "then": {
                "$or": [
                  {
                    "$in": ["$chapters", ["@param:chapter[]"]]
                  },
                  {
                    "$in": ["$chapters", [["@param:chapter[]"]]]
                  }
                ]
              },
              "else": true
            }
          }
        ]
      }
    }
  },
  {
    "$set": {
      "chapters": {
        "$map": {
          "input": "$chapters",
          "as": "ch",
          "in": {
            "$toObjectId": "$$ch"
          }
        }
      }
    }
  },
  {
    "$lookup": {
      "from": "mge-chapters",
      "localField": "chapters",
      "foreignField": "_id",
      "as": "chapters"
    }
  },
  {
    "$set": {
      "course": {
        "$map": {
          "input": "$course",
          "as": "ch",
          "in": {
            "$toObjectId": "$$ch"
          }
        }
      }
    }
  },
  {
    "$lookup": {
      "from": "mge-courses",
      "localField": "course",
      "foreignField": "_id",
      "as": "course"
    }
  },
  {
    "$match": {
      "$expr": {
        "$or": [
          {
            "$eq": ["@param:title", null]
          },
          {
            "$regexMatch": {
              "input": "$title",
              "regex": "@param:title",
              "options": "i"
            }
          }
        ]
      }
    }
  },
  {
    "$sort": {
      "created_at": -1
    }
  },
  {
    "$facet": {
      "meta_data": [
        {
          "$count": "count"
        },
        {
          "$addFields": {
            "skip": "@param:skip",
            "limit": "@param:limit"
          }
        }
      ],
      "data": [
        {
          "$skip": "@param:skip"
        },
        {
          "$limit": "@param:limit"
        }
      ]
    }
  }
]
