queryAdvance:
[
  {
    "$match": {
      "course": "@param:course_id",
      "status": "joined"
    }
  },
  {
    "$lookup": {
      "from": "user-tenant-profile",
      "localField": "user",
      "foreignField": "user",
      "pipeline": [
        {
          "$match": {
            "tenant_id": "@header:x-tenant-id"
          }
        },
        {
          "$addFields": {
            "full_name": {
              "$concat": [
                {
                  "$ifNull": ["$profile.last_name", ""]
                },
                " ",
                {
                  "$ifNull": ["$profile.first_name", ""]
                }
              ]
            }
          }
        },
        {
          "$unwind": {
            "path": "$user",
            "preserveNullAndEmptyArrays": true
          }
        },
        {
          "$project": {
            "_id": "$user",
            "full_name": 1,
            "featured_image": "$profile.course.featured_image",
            "email": 1,
            "department":"$profile.course.department",
            "team":"$profile.course.team",
            "job_position":"$profile.course.job_position"
          }
        }
      ],
      "as": "user"
    }
  },
  {
    "$match": {
      "$expr": {
        "$cond": {
          "if": {
            "$ne": ["@param:role[]", null]
          },
          "then": {
            "$eq": ["$role", "@param:role[]"]
          },
          "else": true
        }
      }
    }
  },
  {
    "$addFields": {
      "role_sort_order": {
        "$switch": {
          "branches": [
            {
              "case": {
                "$eq": ["$role", "instructor"]
              },
              "then": 1
            },
            {
              "case": {
                "$eq": ["$role", "assistant"]
              },
              "then": 2
            },
            {
              "case": {
                "$eq": ["$role", "member"]
              },
              "then": 3
            }
          ],
          "default": 4
        }
      }
    }
  },
  {
    "$unwind": {
      "path": "$user",
      "preserveNullAndEmptyArrays": true
    }
  },
  {
    "$match": {
      "$expr": {
        "$or": [
          {
            "$eq": ["@param:title", null]
          },
          {
            "$regexMatch": {
              "input": "$user.full_name",
              "regex": "@param:title",
              "options": "i"
            }
          },
          {
            "$regexMatch": {
              "input": "$user.email",
              "regex": "@param:title",
              "options": "i"
            }
          }
        ]
      }
    }
  },
  {
    "$addFields": {
      "user": {
        "$cond": [
          {
            "$ne": ["$user", null]
          },
          ["$user"],
          []
        ]
      }
    }
  },
  {
    "$sort": {
      "role_sort_order": 1,
      "created_at": "@sort_param:created_at"
    }
  },
  {
    "$project": {
      "role_sort_order": 0
    }
  },
  {
    "$facet": {
      "meta_data": [
        {
          "$count": "count"
        },
        {
          "$addFields": {
            "skip": "@param:skip",
            "limit": "@param:limit"
          }
        }
      ],
      "data": [
        {
          "$skip": "@param:skip"
        },
        {
          "$limit": "@param:limit"
        }
      ]
    }
  }
]
